import React, { useState } from 'react';
//import './Personalizacion.css';

function Personalizacion() {
  const [titulo, setTitulo] = useState('');
  const [inhalExhal, setInhalExhal] = useState(4);
  const [mantener, setMantener] = useState(4);
  const [ciclos, setCiclos] = useState(1);

    // Componente funcional con Hooks (forma moderna)
    function Sumar(inhalExhal) {
        
        const increment = () => {
            setInhalExhal(count + 1);
        };
        
        return (
            <div>
            <p>Count: {count}</p>
            <button onClick={increment}>+</button>
            </div>
        );
    }

    function Restar(inhalExhal) {
        const [count, setCount] = useState(inhalExhal);
        
        const increment = () => {
            setCount(count + 1);
        };
        
        return (
            <div>
            <p>Count: {count}</p>
            <button onClick={increment}>+</button>
            </div>
        );
    }


  return (
    <div className="personalizacion">
      <div className="personalizacion-fields">
        <input type="text" placeholder="TÃ­tulo de la meditacion" value={titulo} onChange={e => setTitulo(e.target.value)}/>
        <div>
          <span>Inhalacion y exhalacion</span>
          <button onClick={() => setInhalExhal(i => Math.max(1, i - 1))}>-</button>  
          <span>{inhalExhal}</span>
          <button onClick={() => setInhalExhal(i => i + 1)}>+</button>
        </div>
        <div>
          <span>Mantener</span>
          <button onClick={() => setMantener(i => Math.max(1, i - 1))}>-</button>  
          <span>{mantener}</span>
          <button onClick={() => setMantener(i => i + 1)}>+</button>
        </div>
        <div>
          <span>Ciclos</span>
          <input type="number" min="1" value={ciclos} onChange={e => setCiclos(Math.max(1, Number(e.target.value)))}/>
        </div>
      </div>
      <button> Guardar </button>
    </div>
  );
}

export default Personalizacion;
