import React, { useState } from 'react';
//import './Personalizacion.css';

function Personalizacion() {
  const [titulo, setTitulo] = useState('');
  const [inhalExhal, setInhalExhal] = useState(4);
  const [mantener, setMantener] = useState(4);
  const [ciclos, setCiclos] = useState(1);

  const handleGuardar = () => {
    alert(`Guardado:\nTítulo: ${titulo}\nInhalación: ${inhalacion}\nCiclos: ${ciclos}`);
  };

  return (
    <div className="personalizacion">
      <div className="personalizacion-fields">
        <input
          type="text"
          placeholder="Título"
          value={titulo}
          onChange={e => setTitulo(e.target.value)}
          className="personalizacion-input"
        />
        <div className="personalizacion-row">
          <span>Inhalación:</span>
          <button onClick={() => setInhalacion(i => Math.max(1, i - 1))}>-</button>
          <span className="personalizacion-num">{inhalacion}</span>
          <button onClick={() => setInhalacion(i => i + 1)}>+</button>
        </div>
        <div className="personalizacion-row">
          <span>Ciclos:</span>
          <input
            type="number"
            min="1"
            value={ciclos}
            onChange={e => setCiclos(Math.max(1, Number(e.target.value)))}
            className="personalizacion-num-input"
          />
        </div>
      </div>
      <button
        onClick={handleGuardar}
        className="personalizacion-btn"
      >
        Guardar
      </button>
    </div>
  );
}

export default Personalizacion;
